    def chidToItem(self,chid):
        index = (chid%100-1)+int(chid/100)*30 #502 -> 1+5*30=151
        #print(index)
        if index<30:
            item = self.gridLayout_6.itemAt(index).widget()
        elif index<60:
            item = self.gridLayout.itemAt(index-30).widget()
        elif index<90:
            item = self.gridLayout_2.itemAt(index-60).widget()
        elif index<120:
            item = self.gridLayout_3.itemAt(index-90).widget()
        elif index<150:
            item = self.gridLayout_4.itemAt(index-120).widget()
        elif index<180:
            item = self.gridLayout_5.itemAt(index-150).widget()
        
        return index,item 
    
    def bufferAppend(self,chid): ##save selected but not yet saved channel to buffer
        index,item = self.chidToItem(chid)
            
        if self.button_mode[index] == False: 
            self.buffer = np.insert(self.buffer,self.buffer.size,chid)
            self.button_mode[index] = True
            item.setStyleSheet("background-color: rgb(0,0,127);color: rgb(255,255,255)")
            
        elif self.button_mode[index] == True:
            del_index_array = np.where(self.buffer == chid)
            self.buffer = np.delete(self.buffer,del_index_array[0])
            self.button_mode[index] = False
            item.setStyleSheet("background-color: rgb(255,255,255);")
    
    def initProject(self):
        ip_address = str(self.ip1.text()+"."+self.ip2.text()+"."+self.ip3.text()+"."+self.ip4.text())
        runChInformation(ip_address)
        channel_info = pd.read_csv("channel_info_python.csv")
        chinfo = getChannelData(channel_info)
        #----------
        #subunits = getSubUnits(chinfo)
        #channel which are not in chinfo i.e. [001,340,320,421]
        #
        # for chid in channel:
            # index,item = self.chidToItem(chid)
            # item.setEnabled(False)
            # item.hide()
        #----------
        size = int(chinfo.size/15)
        chid = np.array([],int)
        all_channel = np.array([],int)
        
        for i in range(6):
            for j in range(30):
                channel_elem= i*100+j+1
                all_channel = np.append(all_channel,channel_elem)
                
        for i in range(size):
            digit1 = chinfo[2+i*15]-48
            digit2 = chinfo[3+i*15]-48
            digit3 = chinfo[4+i*15]-48
            chid_elem = digit1*100+digit2*10+digit3
            chid = np.append(chid,chid_elem)
        
        
        chid_miss = all_channel
        for i in range(chid.size):
            chid_miss = chid_miss[chid_miss != chid[i]]
        
        for chid_elem in chid_miss: #feature: all missing channels are hidden -> only channel in chinfo are seen
            index,item = self.chidToItem(chid_elem)
            item.setEnabled(False)
            item.hide()
