    def bubbleSort(self,selected_channel):
        for i in selected_channel.size-np.arange(selected_channel.size):
            for j in np.arange(i-1):
                if selected_channel[j]>selected_channel[j+1]:
                    temp = selected_channel[j]
                    selected_channel[j] = selected_channel[j+1]
                    selected_channel[j+1] = temp
        return selected_channel
    
    def setChannel(self): ##after "save" assigns buffer to selected_channel
        self.buffer = self.buffer.astype(int)
        self.selected_channel = self.buffer
