    def setProjectSettings(self):
        self.project_settings=[]
        self.database_settings=[]
        
        print(self.project_name)
        self.project_settings.append(self.project_name)
        self.project_settings.append(self.ip_address)
        self.project_settings.append(float(self.frequency))
        self.project_settings.append(list(self.selected_channel))
        
        if hasattr(self,'ui_si')==True:
            self.signal_names = self.ui_si.signal_names
        
        ##extend signal names to the same length as selected_channel #bug fix
        if self.selected_channel.size != len(self.signal_names):
            diff = self.selected_channel.size - len(self.signal_names)
            for i in np.arange(diff): 
                self.signal_names.append("--")
        ####
        
        self.project_settings.append(self.signal_names)
        
        self.database_settings.append(self.measurement_name)
        self.database_settings.append(self.org)
        self.database_settings.append(self.bucket)
        self.database_settings.append(self.token)
        
        self.project_settings.append(self.database_settings)
            
    def createProjectSettingsFile(self,path):
        filename = path+"/"+"project_settings.proj"
        print(filename)
        f = open(filename,"w")
        print(self.project_settings)
        for settings in self.project_settings:
            f.write(str(settings)+"\n")
        f.close()
        
    def openProject(self):
        self.window = QtWidgets.QMainWindow()
        filename=QtWidgets.QFileDialog.getOpenFileName(self.window,'Open File',r'Projects')
        print(filename[0])
        f = open(filename[0],'r')
        settings = f.read()
        f.close()
        lines = settings.split('\n')
        
        self.project_settings=[] #sets project settings to none
        self.defaultChannel()
        
        self.project_settings.append(lines[0])
        self.project_name_e.setText(lines[0])
        
        self.project_settings.append(lines[1])
        self.ip1.setText(lines[1][:3]) #192
        self.ip2.setText(lines[1][4:7]) #168
        self.ip3.setText(lines[1][8:9]) #0
        self.ip4.setText(lines[1][10:13]) #100
        
        #self.initProject()
        
        self.project_settings.append(float(lines[2]))
        self.frequency_e.setText(str(float(lines[2])))
        
        channel = np.asarray([int(element) for element in lines[3][1:-1].split(', ')])
        self.project_settings.append(channel)
        
        for chid in channel:
            self.bufferAppend(chid)
        
        names = [element[1:-1] for element in lines[4][1:-1].split(', ')]
        self.project_settings.append(names)
        
        datab = [element[1:-1] for element in lines[5][1:-1].split(', ')]
        self.mea_name_e.setText(datab[0])
        self.org_e.setText(datab[1])
        self.bucket_e.setText(datab[2])
        self.token_e.setText(datab[3])
        self.project_settings.append(datab)
        
        
        print("Project Settings:",self.project_settings)
    
